import React, { useState } from "react";
import "../styles/app.css";

const Home = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const languages = ["English", "Spanish", "French", "German", "Chinese", "Japanese", "Korean", "Russian", "Arabic"];

  const handleLanguageSelect = async (language) => {
    alert(`You selected: ${language}`);
    setIsDropdownOpen(false);

    try {
      const response = await fetch("https://your-backend-server.com/api/language", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ language }),
      });

      if (response.ok) {
        console.log("Language posted successfully!");
      } else {
        console.error("Failed to post language.");
      }
    } catch (error) {
      console.error("Error posting language:", error);
    }
  };

  return (
    <div className="app">
      <header className="header">
        <div className="logo">MAX<span className="highlight">PAIN</span></div>
        <nav className="nav">
          <a href="/" className="nav-link">Home</a>
          <a href="#providers" className="nav-link">Providers</a>
          <a href="#attorneys" className="nav-link">Attorneys</a>
          <a href="#about" className="nav-link">About</a>
          <a href="#careers" className="nav-link">Careers</a>
        </nav>
        <div className="actions">
          <div className="dropdown">
            <button className="lang-btn" onClick={toggleDropdown}>
              English
            </button>
            {isDropdownOpen && (
              <ul className="dropdown-menu">
                {languages.map((language, index) => (
                  <li
                    key={index}
                    className="dropdown-item"
                    onClick={() => handleLanguageSelect(language)}
                  >
                    {language}
                  </li>
                ))}
              </ul>
            )}
          </div>
          <a href="#help" className="help-link">Help</a>
          <a href="/login" className="login-link">Log In</a>
          <a href="/signup" className="join-btn">Join MAX</a>
        </div>
      </header>
      <main className="main">
        <div className="hero">
          <h1 className="hero-title">MAXPAIN</h1>
          <p className="hero-subtitle">Connecting you to the right people when things go left.</p>
        </div>
        <div className="branding-section">
          <div className="branding">
            <h2 className="branding-title">GoMAX</h2>
            <p className="branding-description">A Friend when you need one.</p>
          </div>
          <div className="branding">
            <h2 className="branding-title">MAXPAIN</h2>
            <p className="branding-description">Medical Attorney Exchange</p>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Home;
