import React, { useState, useEffect } from "react";
import "../styles/app.css";

const Home = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState("English");
  const [isNavOpen, setIsNavOpen] = useState(false);
  const [lastScrollY, setLastScrollY] = useState(+window.scrollY);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [isDesktop, setIsDesktop] = useState(window.innerWidth > 768);
  const [isTablet, setIsTablet] = useState(window.innerWidth > 768 && window.innerWidth <= 1024);
  const [isTabletLandscape, setIsTabletLandscape] = useState(window.innerWidth > 1024 && window.innerWidth <= 1280);
  const [isTabletPortrait, setIsTabletPortrait] = useState(window.innerWidth > 1280 && window.innerWidth <= 1440);
  const [isLargeDesktop, setIsLargeDesktop] = useState(window.innerWidth > 1440);
  const [isExtraLargeDesktop, setIsExtraLargeDesktop] = useState(window.innerWidth > 1920);
  const [isExtraSmallMobile, setIsExtraSmallMobile] = useState(window.innerWidth <= 320);
  const [isSmallMobile, setIsSmallMobile] = useState(window.innerWidth > 320 && window.innerWidth <= 375);
  const [isMediumMobile, setIsMediumMobile] = useState(window.innerWidth > 375 && window.innerWidth <= 425);
  const [isLargeMobile, setIsLargeMobile] = useState(window.innerWidth > 425 && window.innerWidth <= 480);
  const [isExtraLargeMobile, setIsExtraLargeMobile] = useState(window.innerWidth > 480 && window.innerWidth <= 540);
  const [isExtraExtraLargeMobile, setIsExtraExtraLargeMobile] = useState(window.innerWidth > 540 && window.innerWidth <= 600);
  const [isExtraExtraExtraLargeMobile, setIsExtraExtraExtraLargeMobile] = useState(window.innerWidth > 600 && window.innerWidth <= 640);
  const [isScrollingUp, setIsScrollingUp] = useState(true);
  const [isMenuVisible, setIsMenuVisible] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
    setIsNavOpen(false);  // Always close nav when dropdown opens
    setIsMenuVisible(false); // Close menu when dropdown opens
  };


  const handleScroll = () => {
    const currentScrollY = window.scrollY;
    if (currentScrollY > lastScrollY) {
      setIsScrollingUp(false); // Scrolling down
    } else {
      setIsScrollingUp(true); // Scrolling up
    }
    setLastScrollY(currentScrollY);
  };

  const toggleMenu = () => {
    setIsMenuVisible(!isMenuVisible);
    setIsDropdownOpen(false); // Close dropdown when menu opens
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []); // Only attach listener once

  const languages = [
    "English",
    "Spanish",
    "French",
    "German",
    "Chinese",
    "Japanese",
    "Korean",
    "Russian",
    "Arabic",
  ];

  const handleLanguageSelect = async (language) => {
    setSelectedLanguage(language);
    setIsDropdownOpen(false);
    console.log(language);

    try {
      const response = await fetch("https://your-backend-server.com/api/language", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ language }),
      });

      if (response.ok) {
        console.log("Language posted successfully!");
      } else {
        console.error("Failed to post language.");
      }
    } catch (error) {
      console.error("Error posting language:", error);
    }
  };

  return (
    <div>
      <header className="header">
        <button onClick={toggleMenu} onResize={isDesktop} className="hamburger-btn" aria-label="Toggle menu">
          <img src="/images/menu.png" alt="Menu" className="hamburger-icon" />
        </button>
        <div className="logo">
          <a href="/" className="logo-link">
            MAX<span className="highlight">PAIN</span>
          </a>
        </div>
        {isMenuVisible && (
          <>
            <nav className="nav">
              <a href="/">Friends</a>
              <a href="/investor">Investors</a>
              <a href="/attorneys">Attorneys</a>
              <a href="/about">About</a>
              <a href="/careers">Careers</a>
            </nav>
            <div className="actions">
              <a href="/contact-us" className="help-link">help</a>
              <a href="/Login" className="help-link">Log In</a>
              <a href="/signup" className="join-btn">Join MAX</a>
            </div>
          </>
        )}
        <div className="dropdown">
          <button className="lang-btn" onClick={toggleDropdown} aria-label="Select language">
            <img src="/images/lang-icon.png" alt="Language" className="flag-icon" />
            <span className="lang-text">{selectedLanguage}</span>
          </button>
          {isDropdownOpen && (
            <ul className="dropdown-menu">
              {languages.map((language, index) => (
                <li
                  key={index}
                  className="dropdown-item"
                  onClick={() => handleLanguageSelect(language)}
                >
                  {language}
                </li>
              ))}
            </ul>
          )}
        </div>
      </header>
      <main className="main">
        <div className="hero">
          <div className="branding">
            <div className="hero-content">
              <h1 className="hero-title">
                <span className="hero-max">M</span>
                <span className="hero-axpain">AXPAIN</span>
              </h1>
              <div className="hero-line"></div>
              <div className="hero-subtext">MEDICAL ATTORNEY EXCHANGE</div>
            </div>
            <p className="branding-subtitle">
              Connecting you to the right people when things go left.
            </p>
          </div>
        </div>
      </main>
      <div className="bottom-right-logo">
        <img src="/images/app-logo.png" alt="Logo" />
      </div>
    </div>
  );
};

export default Home;
