import i18n from "i18next";
import { initReactI18next } from "react-i18next";

const resources = {
  English: {
    translation: {
      welcome: "Welcome to MAXPAIN",
      subtitle: "Connecting you to the right people when things go left.",
      help: "Help",
      login: "Log In",
      join: "Join MAX",
    },
  },
  Spanish: {
    translation: {
      welcome: "Bienvenido a MAXPAIN",
      subtitle: "Conectándote con las personas adecuadas cuando las cosas van mal.",
      help: "Ayuda",
      login: "Iniciar sesión",
      join: "Únete a MAX",
    },
  },
  French: {
    translation: {
      welcome: "Bienvenue chez MAXPAIN",
      subtitle: "Vous connecter aux bonnes personnes lorsque les choses tournent mal.",
      help: "Aide",
      login: "Se connecter",
      join: "Rejoindre MAX",
    },
  },
};

i18n.use(initReactI18next).init({
  resources,
  lng: "English", // Default language
  fallbackLng: "English",
  interpolation: {
    escapeValue: false, // React already escapes values
  },
});

export default i18n;
