import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import "../styles/app.css";

const Home = () => {
  const { t, i18n } = useTranslation(); // Hook for translations
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState("English");

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const languages = ["English", "Spanish", "French"];

  const handleLanguageSelect = (language) => {
    setSelectedLanguage(language); // Update the selected language
    i18n.changeLanguage(language); // Change the language in i18next
    setIsDropdownOpen(false); // Close the dropdown
  };

  return (
    <div className="app">
      <header className="header">
        <div className="logo">MAX<span className="highlight">PAIN</span></div>
        <nav className="nav">
          <a href="#attorneys">{t("welcome")}</a>
        </nav>
        <div className="actions">
          <div className="dropdown">
            <button className="lang-btn" onClick={toggleDropdown}>
              <img src="/images/languagelogo.png" alt="Language" className="flag-icon" />
              <span className="lang-text">{selectedLanguage}</span>
            </button>
            {isDropdownOpen && (
              <ul className="dropdown-menu">
                {languages.map((language, index) => (
                  <li
                    key={index}
                    className="dropdown-item"
                    onClick={() => handleLanguageSelect(language)}
                  >
                    {language}
                  </li>
                ))}
              </ul>
            )}
          </div>
          <a href="/contact-us" className="help-link">{t("help")}</a>
          <a href="/login" className="help-link">{t("login")}</a>
          <a href="/signup" className="join-btn">{t("join")}</a>
        </div>
      </header>
      <main className="main">
        <div className="hero">
          <h1 className="hero-title">{t("welcome")}</h1>
          <p className="hero-subtitle">{t("subtitle")}</p>
        </div>
      </main>
    </div>
  );
};

export default Home;
