import React, { useEffect, useState } from "react";
import "../styles/products.css";

const Products = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch("https://imdb236.p.rapidapi.com/imdb/lowest-rated-movies", {
          method: "GET",
          headers: {
            "X-RapidAPI-Key": "YOUR_API_KEY", // Replace with your actual API key
            "X-RapidAPI-Host": "imdb236.p.rapidapi.com",
          },
        });
        const data = await response.json();
        console.log(data); // Inspect the response structure
        setMovies(data.results || []); // Use a fallback if `results` is undefined
        setLoading(false);
      } catch (error) {
        console.error("Error fetching movies:", error);
        setLoading(false);
      }
    };

    fetchMovies();
  }, []);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="products-page">
      <h1 className="products-title">Our Products</h1>
      <div className="products-grid">
        {movies && movies.length > 0 ? (
          movies.map((movie) => (
            <div className="product-card" key={movie.id}>
              <img
                src={movie.poster_path ? `https://image.tmdb.org/t/p/w500${movie.poster_path}` : "https://via.placeholder.com/150"}
                alt={movie.title}
                className="product-image"
              />
              <h2 className="product-name">{movie.title}</h2>
              <p className="product-description">{movie.overview?.substring(0, 100)}...</p>
            </div>
          ))
        ) : (
          <p>No movies available.</p>
        )}
      </div>
    </div>
  );
};

export default Products;
